void SURFERDATA_Load(void)
{
    nglFileBuf F;
    KSReadFile("surferdata.dat", &F, 1);
    char *s = (char *) F.Buf;
    const char *format = "%[^,], %[^,], %[^,], %[^,], %[^,], %[^,], %[^,], %d, %d, %d, %d, %d, %d, %d, %d, %[^,], %[^,], %d, %d, %d, %[^,], %d, ";
    for (int i = 0; i < 14; ++i)
    {
         char *p = strchr (s, '\n');
         if (p) *p = 0;
         int n = sscanf(s, format,
              &SurferDataArray[i].name,
              &SurferDataArray[i].name_ps,
              &SurferDataArray[i].stashfile,
              &SurferDataArray[i].firstname,
              &SurferDataArray[i].lastname,
              &SurferDataArray[i].fullname,
              &SurferDataArray[i].abbr,
              &SurferDataArray[i].goofy,
              &SurferDataArray[i].load_in_fe,
              &SurferDataArray[i].bio_length,
              &SurferDataArray[i].has_movie,
              &SurferDataArray[i].attr_spin,
              &SurferDataArray[i].attr_speed,
              &SurferDataArray[i].attr_balance,
              &SurferDataArray[i].attr_air,
              &SurferDataArray[i].board_ent_name,
              &SurferDataArray[i].p_board_name,
              &SurferDataArray[i].selectable,
              &SurferDataArray[i].competitor,
              &SurferDataArray[i].initially_unlocked,
              &SurferDataArray[i].surfer_nick,
              &SurferDataArray[i].sort_order
         );
         if (strcmp(SurferDataArray[i].lastname, "-")==0) SurferDataArray[i].lastname[0] = '\0';
         if (p) *p = '\n';
         assert(22 == n);
         verify((s = strchr(s, '\n')) != NULL);
         ++s;
    }
    KSReleaseFile(&F);
}
