void TRICKDATA_LoadButtons(void)
{

    button_map buttons[19];

    buttons[0].str="PAD_U"; buttons[0].button=PAD_U;
    buttons[1].str="PAD_D"; buttons[1].button=PAD_D;
    buttons[2].str="PAD_L"; buttons[2].button=PAD_L;
    buttons[3].str="PAD_R"; buttons[3].button=PAD_R;
    buttons[4].str="PAD_UL"; buttons[4].button=PAD_UL;
    buttons[5].str="PAD_UR"; buttons[5].button=PAD_UR;
    buttons[6].str="PAD_DL"; buttons[6].button=PAD_DL;
    buttons[7].str="PAD_DR"; buttons[7].button=PAD_DR;
    buttons[8].str="PAD_CIRCLE"; buttons[8].button=PAD_CIRCLE;
    buttons[9].str="PAD_CROSS"; buttons[9].button=PAD_CROSS;
    buttons[10].str="PAD_TRIANGLE"; buttons[10].button=PAD_TRIANGLE;
    buttons[11].str="PAD_SQUARE"; buttons[11].button=PAD_SQUARE;
    buttons[12].str="PAD_L1"; buttons[12].button=PAD_L1;
    buttons[13].str="PAD_L2"; buttons[13].button=PAD_L2;
    buttons[14].str="PAD_R1"; buttons[14].button=PAD_R1;
    buttons[15].str="PAD_R2"; buttons[15].button=PAD_R2;
    buttons[16].str="PAD_NONE"; buttons[16].button=PAD_NONE;
    buttons[17].str="PAD_RELEASE"; buttons[17].button=PAD_RELEASE;
    buttons[18].str="PAD_LAUNCH"; buttons[18].button=PAD_LAUNCH;

    for (int n = 0; n < TRICK_NUM; n++)
    {
         for (int m = 0; m < ((signed) countof(buttons)); m++)
         {
              if (GTrickList[n].button1_string == buttons[m].str)
                   GTrickList[n].button1 = buttons[m].button;

              if (GTrickList[n].button2_string == buttons[m].str)
                   GTrickList[n].button2 = buttons[m].button;

              if (GTrickList[n].button3_string == buttons[m].str)
                   GTrickList[n].button3 = buttons[m].button;
         }
    }

}


void TRICKDATA_Load(void)
{
    nglFileBuf F;
    KSReadFile("trickdata.dat", &F, 1);
    char *s = (char *) F.Buf;
    const char *format = "%[^,], %[^,], %[^,], %d, %d, ";
    for (int i = 0; i < 95; ++i)
    {
         char *p = strchr (s, '\n');
         if (p) *p = 0;
         int n = sscanf(s, format,
              &GTrickList[i].button1_string,
              &GTrickList[i].button2_string,
              &GTrickList[i].button3_string,
              &GTrickList[i].Time,
              &GTrickList[i].Points
         );
         if (p) *p = '\n';
         assert(5 == n);
         verify((s = strchr(s, '\n')) != NULL);
         ++s;
    }
    KSReleaseFile(&F);

    TRICKDATA_LoadButtons();

}
